mapper.update:
UPDATE tb_refund SET
    ware_no = #{ware_no},
    refund_confirm_cd = 1            

	AND pro_no = #{pro_no}
	AND refund_no = #{refund_no}
	AND ware_no = #{origin_ware_no}

UPDATE tb_direction set
    ware_no = #{ware_no},
    direction_manager = #{loginId}        

	AND pro_no = #{pro_no}
	AND refund_no = #{refund_no}
	AND ware_no = #{origin_ware_no}
