mapper.update:
UPDATE tb_userinfo
	SET 
	  name = #{name}
	, user_type = "C"
	, password = #{password}
	, user_tel1 = #{tel1}
	, user_tel2 = #{tel2}
	, user_tel3 = #{tel3}
	, user_email = #{email}
	, user_zipcode = #{zipcode}
	, user_address = #{address}
	, user_dt_address = #{dt_address}
WHERE loginID = #{loginID}
