mapper.select:
SELECT userinfo. name, DATE_FORMAT( inquiry. inq_regdate, "%y년 %c월 %e일 %H시 %i분" ) as inq_regdate, inquiry. inq_title, inquiry. inq_content, answer.ans_title, answer.ans_content
FROM tb_inquiry inquiry LEFT JOIN tb_userinfo userinfo ON inquiry. loginID = userinfo. loginID LEFT JOIN tb_answer answer ON inquiry.inq_no = answer.inq_no
WHERE inquiry. inq_no = #{inq_no}

mapper.update:
UPDATE tb_inquiry 
       SET answer_cd = #{answer_cd}
WHERE inq_no = #{inq_no}
